    /*
     * *************************************
     * 				 CREAR (C)
     * *************************************
     */
    @PostMapping("/add")
    public ResponseEntity<?> crearCliente(@RequestBody Cliente cliente) {
        Map<String, Object> response = new HashMap<>();
        LocalDate ahora = null;
        Matcher mather = null;

        try {
            // Se valida si nombre y email tienen informacion
            if(cliente.getNombre().isEmpty() || cliente.getEmail().isEmpty() ){
                response.put("Mensaje", "El email y nombre deben tener informacion.");
                return new ResponseEntity<Map<String, Object>>(response, HttpStatus.BAD_REQUEST);
            }else {
                // Se valida si el email tiene un formato valido
                mather = pattern.matcher(cliente.getEmail());

                if(mather.matches()) {
                    //Ingreso un email valido
                    ahora = LocalDate.now();
                    cliente.setCreateAt(ahora);
                    cliente.setUpdateAt(ahora);
                } else {
                    response.put("Mensaje", "El email ingresado no tiene un formato valido.");
                    return new ResponseEntity<Map<String, Object>>(response, HttpStatus.BAD_REQUEST);
                }
            }

        } catch (DataAccessException e) {
            response.put("Mensaje", "Error del servidor.");
            response.put("Error", e.getMessage().concat(": ").concat(e.getMostSpecificCause().getMessage()));
            return new ResponseEntity<Map<String, Object>>(response, HttpStatus.INTERNAL_SERVER_ERROR);
        }

        return clienteSevicio.crearCliente(cliente);
    }
